package oj.hot100;

import java.util.Arrays;

public class TaskScheduler {
    public int leastInterval(char[] tasks, int n) {
        if (n == 0) return tasks.length;
        int res = 0;
        Integer[] taskNum = new Integer[26];
        Arrays.fill(taskNum, 0);
        for (char c : tasks) {
            taskNum[(c - 'A')] += 1;
        }
        Arrays.sort(taskNum, (a, b) -> b - a);
        while (n<26&&taskNum[n] > 0) {
            for (int i = 0; i <= n; i++) {
                taskNum[i]--;
            }
            res += (n + 1);
            Arrays.sort(taskNum, (a, b) -> b - a);
        }

        if (taskNum[0] > 0) {
            res += (taskNum[0] * (n + 1));
            res -= n;
            for (int i = 1; i < taskNum.length; i++) {
                if (taskNum[i] .equals( taskNum[0])) res++;
                else break;
            }
        }

        return res;
    }

    public static void main(String[] args) {
        System.out.println(new TaskScheduler().leastInterval("".toCharArray(), 80));
    }
}
